import { useState, useEffect } from "react";
import { Form, Row, Col, Button, Container } from 'react-bootstrap';
import { BagPlusFill } from 'react-bootstrap-icons';
import BarraBusca from "../meusComponentes/busca/BarraBusca";
import CaixaSelecao from "../meusComponentes/busca/CaixaSelecao";
import TabelaItensVenda from "../tabelas/TabelaItensVenda";
import './FormCadVenda.css';

export default function FormCadVenda() {
    const [validado, setValidado] = useState(false);
    const [listaClientes, setListaClientes] = useState([]);
    const [clienteSelecionado, setClienteSelecionado] = useState({});
    const [produtoSelecionado, setProdutoSelecionado] = useState({});
    const [qtdItem, setQtdItem] = useState(0);
    const [subTotalCalculado, setSubTotalCalculado] = useState(0.00);
    const [venda, setVenda] = useState({
        id: 0,
        dataPedido: "",
        totalPedido: 0,
        cliente: clienteSelecionado,
        itens: []
    });

    // Fetch clientes
    useEffect(() => {
        const fetchClientes = async () => {
            try {
                const response = await fetch('http://localhost:3001/cliente');
                const data = await response.json();
                setListaClientes(data);
            } catch (error) {
                alert("Não foi possível recuperar os clientes do backend.");
            }
        };
        fetchClientes();
    }, []);

    const manipularMudanca = (e) => {
        const { name, value, type, checked } = e.target;
        setVenda({
            ...venda,
            [name]: type === "checkbox" ? checked : value
        });
    };

    const calcularSubtotal = (qtd, preco) => {
        if (!isNaN(qtd) && !isNaN(preco)) {
            return qtd * parseFloat(preco);
        }
        return 0;
    };

    const adicionarItem = () => {
        if (qtdItem > 0 && produtoSelecionado?.codigo) {
            const novoItem = {
                codigo: produtoSelecionado.codigo,
                nome: produtoSelecionado.nome,
                quantidade: qtdItem,
                precoUnitario: produtoSelecionado.precoVenda,
                subtotal: subTotalCalculado
            };

            setVenda(prevVenda => ({
                ...prevVenda,
                itens: [...prevVenda.itens, novoItem],
                totalPedido: prevVenda.totalPedido + subTotalCalculado
            }));
            setQtdItem(0); // Reseta a quantidade para evitar duplicações.
            setSubTotalCalculado(0); // Reseta o subtotal.
        } else {
            alert("Por favor, selecione um produto e defina a quantidade.");
        }
    };

    const gravarVenda = async () => {
        if (!venda.dataPedido || Object.keys(clienteSelecionado).length === 0) {
            alert("Por favor, preencha todos os campos obrigatórios.");
            return;
        }

        const vendaParaEnviar = {
            dataPedido: venda.dataPedido,
            cliente: clienteSelecionado,
            totalPedido: venda.totalPedido,
            itens: venda.itens
        };

        try {
            const response = await fetch('http://localhost:3001/pedido', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(vendaParaEnviar)
            });
            const dados = await response.json();
            if (dados.status) {
                setVenda({ ...venda, id: dados.codigo });
            }
            alert(dados.mensagem);
        } catch (error) {
            alert("Erro ao gravar a venda: " + error.message);
        }
    };

    const manipulaSubmissao = (event) => {
        event.preventDefault();
        event.stopPropagation();

        if (event.currentTarget.checkValidity()) {
            setValidado(false);
            gravarVenda();
        } else {
            setValidado(true);
        }
    };

    return (
        <Container className="border p-4">
            <h2>Cadastro de Venda <BagPlusFill id="icone" size={26} color="black" /></h2>
            <Form noValidate validated={validado} onSubmit={manipulaSubmissao}>
                <Container id="container1">
                    <Row className="mb-3">
                        <Form.Group as={Col} md="4" controlId="idVenda">
                            <Form.Label>Pedido nº</Form.Label>
                            <Form.Control
                                required
                                type="text"
                                placeholder="0"
                                value={venda.id}
                                name="id"
                                disabled
                            />
                        </Form.Group>
                    </Row>
                    <Row className="mb-3">
                        <Form.Group as={Col} md="6" controlId="dataVenda">
                            <Form.Label>Data do Pedido</Form.Label>
                            <Form.Control
                                type="date"
                                required
                                name="dataPedido"
                                value={venda.dataPedido}
                                onChange={manipularMudanca}
                            />
                            <Form.Control.Feedback type="invalid">
                                Por favor informe a data da venda.
                            </Form.Control.Feedback>
                        </Form.Group>
                        <Form.Group as={Col} md="3" controlId="totalPedido">
                            <Form.Label>Total do Pedido</Form.Label>
                            <Form.Control
                                type="text"
                                placeholder="0,00"
                                value={venda.totalPedido}
                                name="totalPedido"
                                disabled
                            />
                        </Form.Group>
                    </Row>
                    <Row>
                        <Form.Group as={Col} md="12" controlId="clienteSelecionado">
                            <Form.Label>Cliente</Form.Label>
                            <BarraBusca
                                campoBusca="nome"
                                campoChave="codigo"
                                dados={listaClientes}
                                funcaoSelecao={setClienteSelecionado}
                                placeHolder="Selecione um cliente"
                            />
                        </Form.Group>
                    </Row>
                </Container>

                <h3 id="titulo2">Itens da Venda</h3>
                <Container id="container2">
                    <Row className="m-3">
                        <Col md={2}>
                            <Form.Label>Selecione um livro</Form.Label>
                        </Col>
                        <Col>
                            <CaixaSelecao
                                enderecoFonteDados="http://localhost:3001/livro"
                                campoChave="codigo"
                                campoExibicao="nome"
                                funcaoSelecao={setProdutoSelecionado}
                                localLista="listaLivros"
                            />
                        </Col>
                    </Row>
                    <Row>
                        <Col md={10}>
                            <Row>
                                <Col md={1}>
                                    <Form.Group>
                                        <Form.Label>Código</Form.Label>
                                        <Form.Control type="text" value={produtoSelecionado?.codigo || ''} disabled />
                                    </Form.Group>
                                </Col>
                                <Col md={4}>
                                    <Form.Group>
                                        <Form.Label>Nome do livro</Form.Label>
                                        <Form.Control type="text" value={produtoSelecionado?.nome || ''} disabled />
                                    </Form.Group>
                                </Col>
                                <Col md={2}>
                                    <Form.Group>
                                        <Form.Label>Preço R$</Form.Label>
                                        <Form.Control type="text" value={produtoSelecionado?.precoVenda || ''} disabled />
                                    </Form.Group>
                                </Col>
                                <Col md={2}>
                                    <Form.Group>
                                        <Form.Label>Qtd</Form.Label>
                                        <Form.Control
                                            type="number"
                                            min={1}
                                            value={qtdItem}
                                            onChange={(e) => {
                                                const qtdInformada = e.target.value;
                                                setQtdItem(qtdInformada);
                                                setSubTotalCalculado(calcularSubtotal(qtdInformada, produtoSelecionado?.precoVenda));
                                            }}
                                        />
                                    </Form.Group>
                                </Col>
                                <Col md={2}>
                                    <Form.Group>
                                        <Form.Label>SubTotal</Form.Label>
                                        <Form.Control type="text" value={subTotalCalculado} disabled />
                                    </Form.Group>
                                </Col>
                                <Col md={1} className="middle">
                                    <Form.Group>
                                        <Form.Label>Adicionar</Form.Label>
                                        <Button id="btn1" onClick={adicionarItem}>
                                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-bag-plus-fill" viewBox="0 0 16 16">
                                                <path fillRule="evenodd" d="M10.5 3.5a2.5 2.5 0 0 0-5 0V4h5v-.5zm1 0V4H15v10a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2V4h3.5v-.5a3.5 3.5 0 1 1 7 0zM8.5 8a.5.5 0 0 0-1 0v1.5H6a.5.5 0 0 0 0 1h1.5V12a.5.5 0 0 0 1 0v-1.5H10a.5.5 0 0 0 0-1H8.5V8z"/>
                                            </svg>
                                        </Button>
                                    </Form.Group>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                    <Row className="mb-3">
                        <TabelaItensVenda itens={venda.itens} />
                    </Row>
                </Container>
                <Row className="mb-3">
                    <Col md={12} className="text-center">
                        <Button type="submit" className="btn btn-success">Salvar Venda</Button>
                    </Col>
                </Row>
            </Form>
        </Container>
    );
}
